function likePost(n,t){$.ajax({type:"GET",url:"/Posts/LikePost",data:{postId:n},dataType:"json",success:function(i){i?(console.log("User liked post."),updatePostLikesCount(n,t)):console.log("Unsuccessful attempt at liking a post.")}})}function dislikePost(n,t){$.ajax({type:"GET",url:"/Posts/DislikePost",data:{postId:n},dataType:"json",success:function(i){i?(console.log("User disliked post."),updatePostLikesCount(n,t)):console.log("Unsuccessful attempt at disliking a post.")}})}function updatePostLikesCount(n,t){$.ajax({type:"GET",url:"/Posts/GetPostLikesCount",data:{postId:n},dataType:"json",success:function(n){n!=-1?t.querySelector("#postLikesCount").textContent=`Like(${n})`:console.log("Unsuccessful attempt at updating post likes count.")}})}document.querySelector("#submitPost").addEventListener("click",()=>{document.querySelector("#postContent").value==""&&alert("You cannot create an empty post.")});let btns=Array.from(document.querySelectorAll("#editPostButton"));btns.forEach(n=>n.addEventListener("click",n=>{let t=n.currentTarget.parentNode.parentNode.parentNode.parentNode.parentNode,i=t.querySelector("p#content").textContent,r=n.currentTarget.querySelector("#hiddenId").textContent;document.querySelector("#modalArea").textContent=i;document.querySelector("#postId").value=r}));let likeButtons=[...document.querySelectorAll("#likeButton")];likeButtons.forEach(n=>n.addEventListener("click",n=>{let t=n.currentTarget,i=t.attributes.itemid.value,r=t.parentNode;console.log(i);console.log(r);t.attributes.fill.value=="none"?(t.attributes.fill.value="red",likePost(i,r)):(t.attributes.fill.value="none",dislikePost(i,r))}));